export const initialState = {
    user: null,
    errorMessage: null,
    isLoading: false,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case 'LOG_IN_REQUEST':
            return { ...state, isLoading: true, errorMessage: null };
        case 'LOG_IN_SUCCESS':
            return {
                ...state,
                isLoading: false,
                user: { name: payload.email },
                errorMessage: null,
            };
        case 'LOG_IN_FAILURE':
            return {
                ...state,
                isLoading: false,
                errorMessage: payload.errorMessage,
            };
        case 'LOG_OUT':
            return { ...state, user: null, errorMessage: null };
        default:
            return state;
    }

};