import React from 'react';
import PropTypes from 'prop-types';
import { Layout } from 'antd';

const { Content } = Layout;

function News({ items }) {

    return (
        <Content
            className="site-layout-background"
            style={{
                margin: '24px 16px',
                padding: 24,
                minHeight: 280,
            }}
        >
            {items.map((item) => (
                <div key={item.id}>
                    <h1>{item.title}</h1>
                    <p>{item.text}</p>
                </div>
            ))}
        </Content>
    );
}

News.propTypes = {
    items: PropTypes.array.isRequired,
};

export default News;