import { createSelector } from 'reselect';

const newsItemsSelector = (state) => state.news.data;
const newsIsLoadingSelector = (state) => state.news.isLoading;
const newsErrorMessageSelector = (state) => state.news.errorMessage;

export const getAll = createSelector(
    newsItemsSelector,
    newsIsLoadingSelector,
    newsErrorMessageSelector,
    (data, isLoading, errorMessage) => ({
        data, isLoading, errorMessage,
    }),
);